mapper.select:
select analysis content, id, difficulty grade, subject title, type, analysisVideo video, '' videoPoster, (select tag from tr_member_question where questionId = tq.id and memberId = #{memberId}) answerTag, (select isFavorite from tr_member_question where questionId = tq.id and memberId =#{memberId} ) isFavorite, IF((select tag from tr_member_question where questionId = tq.id and memberId = #{memberId}) is null, 0, 1) isFinished
,(select isFamous from t_company where id = #{categoryID}) isFamous
from t_question tq
where catalogId = #{categoryID}
where companyId = #{categoryID}

mapper.update:
Update tr_member_question
set isFavorite = #{isFavorite},tag = #{tag},answerResult = #{answerResult}
where memberId = #{memberId} and questionId = #{id}
